/**
 * Object that represents the data neccesary to build an SPUser object.
 *
 * @internal
 */
export interface ISPUserData {
    displayName: string;
    email: string;
    loginName: string;
}
/**
  * This class is primarily used with the PageContext class.  It provides contextual information
  * for the SharePoint user that is accessing the page.
  *
  * @public
  */
export default class SPUser {
    private _displayName;
    private _loginName;
    private _email;
    /**
     * @internal
     */
    constructor(data: ISPUserData);
    /**
     * The display name for the current user.
     * Example: "John Doe"
     */
    displayName: string;
    /**
     * The email address for the current user.
     * Example: "example@contoso.com"
     */
    email: string;
    /**
     * The login name for current user.
     * Example: "i:0#.w|domain\user"
     */
    loginName: string;
}
