import Guid from '../guid/Guid';
import SPPermission from '../permission/SPPermission';
import { IODataBasePermission } from '../odataTypes/IODataBasePermission';
/**
 * Object that represents the data neccesary to build an SPWeb object.
 * @internal
 */
export interface ISPWebData {
    absoluteUrl: string;
    id: string;
    permissions: IODataBasePermission;
    serverRelativeUrl: string;
    title: string;
}
/**
  * This class is primarily used with the PageContext class.  It provides contextual
  * information for the SharePoint site ("web") that hosts the page.
  * @public
  */
export default class SPWeb {
    private _absoluteUrl;
    private _id;
    private _permissions;
    private _serverRelativeUrl;
    private _title;
    /**
     * @internal
     */
    constructor(data: ISPWebData);
    /**
     * Returns the absolute URL for this SPWeb.
     * Example: "https://example.com/sites/PubSite/SubWeb"
     */
    absoluteUrl: string;
    /**
     * The GUID that identifies the SPWeb on the server. This property could be undefined
     * if the information isn't avalible.
     */
    id: Guid;
    /**
     * Return the SPPermission object that represents the set of permissions that the
     * current user has for interacting with the web.
     */
    permissions: SPPermission;
    /**
     * Returns the server-relative URL for this SPWeb.
     * Example: "/sites/PubSite/SubWeb"
     */
    serverRelativeUrl: string;
    /**
     * Returns the title of the SharePoint site.
     */
    title: string;
}
